const BASE = "http://localhost:8080/api/pokemon";
export async function fetchAll() {
  const r = await fetch(BASE);
  return r.json();
}
export async function createPokemon(payload) {
  const r = await fetch(BASE, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(payload),
  });
  return r.json();
}

//POST Function
export async function addPokemon(pokemon) {
  const res = await fetch("http://localhost:8080/api/pokemon", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(pokemon),
  });
  return res.json();
}
