import React from "react";

export default function PokemonFilter({ search, setSearch, type, setType }) {
  const types = [
    "ALL",
    "NORMAL",
    "FIRE",
    "WATER",
    "GRASS",
    "ELECTRIC",
    "PSYCHIC",
    "GHOST",
    "DARK",
    "ROCK",
    "STEEL",
  ];

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        gap: "16px",
        marginBottom: "20px",
      }}
    >
      {/* Search Input */}
      <input
        type="text"
        placeholder="Search PokÃ©mon..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{
          padding: "8px 12px",
          borderRadius: "8px",
          border: "1px solid #ccc",
          minWidth: "200px",
        }}
      />

      {/* Type Dropdown */}
      <select
        value={type}
        onChange={(e) => setType(e.target.value)}
        style={{
          padding: "8px 12px",
          borderRadius: "8px",
          border: "1px solid #ccc",
        }}
      >
        {types.map((t) => (
          <option key={t} value={t}>
            {t}
          </option>
        ))}
      </select>
    </div>
  );
}
