import React from "react";
import "../styles/card.css";

export default function PokemonCard({ p }) {
  return (
    <div className="card">
      <img
        src={p.imageUrl}
        alt={p.name}
        onError={(e) => (e.target.src = "/fallback.png")}
      />
      <h3>{p.name}</h3>
      <p>{p.description}</p>
      <div className={`type ${p.type}`}>{p.type}</div>
      <div>HP: {p.hp}</div>
    </div>
  );
}
