import React, { useEffect, useState } from "react";
import { fetchAll } from "../api/pokemonApi";
import PokemonCard from "../components/PokemonCard";
import PokemonFilter from "../components/PokemonFilter";
import AddPokemonForm from "../components/AddPokemonForm";

export default function PokemonPage() {
  const [list, setList] = useState([]); // Pokémon array
  const [loading, setLoading] = useState(true); // loading state
  const [error, setError] = useState(null); // error message
  const [search, setSearch] = useState("");
  const [type, setType] = useState("ALL");

  // Fetch Pokémon list
  useEffect(() => {
    setLoading(true);
    setError(null);

    fetchAll()
      .then((data) => {
        if (Array.isArray(data)) {
          setList(data);
        } else {
          console.error("Expected array but got:", data);
          setList([]);
          setError("Unexpected response from server");
        }
      })
      .catch((err) => {
        console.error(err);
        setError("Failed to fetch Pokémon from backend");
        setList([]);
      })
      .finally(() => setLoading(false));
  }, []);

  const handleAdded = (newPokemon) => {
    if (newPokemon && typeof newPokemon === "object") {
      setList((prev) => [...prev, newPokemon]);
    }
  };

  // Apply search + type filter
  const filteredList = Array.isArray(list)
    ? list.filter((p) => {
        const matchesSearch = p.name
          .toLowerCase()
          .includes(search.toLowerCase());
        const matchesType = type === "ALL" || p.type === type;
        return matchesSearch && matchesType;
      })
    : [];

  return (
    <div>
      <h1 style={{ textAlign: "center", marginTop: 20 }}>Pokémon Collection</h1>

      <PokemonFilter
        search={search}
        setSearch={setSearch}
        type={type}
        setType={setType}
      />

      <AddPokemonForm onAdded={handleAdded} />

      {loading && <p style={{ textAlign: "center" }}>Loading Pokémon...</p>}
      {error && <p style={{ textAlign: "center", color: "red" }}>{error}</p>}

      {!loading && !error && (
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(220px, 1fr))",
            gap: "16px",
            padding: "20px",
          }}
        >
          {filteredList.map((p) => (
            <PokemonCard key={p.id} p={p} />
          ))}
        </div>
      )}

      {!loading && !error && filteredList.length === 0 && (
        <p style={{ textAlign: "center" }}>No Pokémon found.</p>
      )}
    </div>
  );
}
