import React, { useEffect, useState } from "react";
import { fetchAll } from "../api/pokemonApi";
import PokemonCard from "../components/PokemonCard";
import PokemonFilter from "../components/PokemonFilter";
import AddPokemonForm from "../components/AddPokemonForm";

export default function PokemonPage() {
  const [list, setList] = useState([]);
  const [search, setSearch] = useState("");
  const [type, setType] = useState("ALL");

  // When setting list, ensure it’s an array
  useEffect(() => {
    fetchAll()
      .then((data) => setList(Array.isArray(data) ? data : []))
      .catch((err) => {
        console.error(err);
        setList([]); // fallback
      });
  }, []);
  const handleAdded = (newPokemon) => {
    setList([...list, newPokemon]); // append new Pokémon
  };

  // Apply search + filter
  const filteredList = list.filter((p) => {
    const matchesSearch = p.name.toLowerCase().includes(search.toLowerCase());
    const matchesType = type === "ALL" || p.type === type;
    return matchesSearch && matchesType;
  });

  return (
    <div>
      <h1 style={{ textAlign: "center", marginTop: 20 }}>Pokémon Collection</h1>

      <PokemonFilter
        search={search}
        setSearch={setSearch}
        type={type}
        setType={setType}
      />

      <AddPokemonForm onAdded={handleAdded} />

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(220px, 1fr))",
          gap: "16px",
          padding: "20px",
        }}
      >
        {filteredList.map((p) => (
          <PokemonCard key={p.id} p={p} />
        ))}
      </div>
    </div>
  );
}
