const BASE = "http://localhost:8080/api/pokemons";

// helper to get headers with JWT
function getAuthHeaders() {
  const token = localStorage.getItem("token");
  return {
    "Content-Type": "application/json",
    Authorization: `Bearer ${token}`,
  };
}

export async function createPokemon(payload) {
  const res = await fetch(BASE, {
    method: "POST",
    headers: getAuthHeaders(),
    body: JSON.stringify(payload),
  });
  if (!res.ok) throw new Error("Failed to create Pokémon");
  return res.json();
}

export async function addPokemon(pokemon) {
  const res = await fetch(`${BASE}/add`, {
    method: "POST",
    headers: getAuthHeaders(),
    body: JSON.stringify(pokemon),
  });
  if (!res.ok) throw new Error("Failed to add Pokémon");
  return res.json();
}

export async function fetchAll() {
  const res = await fetch(BASE, {
    method: "GET",
    headers: getAuthHeaders(),
  });
  if (!res.ok) throw new Error("Failed to fetch Pokémon");
  return res.json(); // must be an array
}
