import React, { useState } from "react";
import { createPokemon } from "../api/pokemonApi";

export default function AddPokemonForm({ onAdded }) {
  const [form, setForm] = useState({
    name: "",
    description: "",
    type: "NORMAL",
    hp: 50,
    imageUrl: "",
  });
  const [error, setError] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newPokemon = await createPokemon(form);
      onAdded(newPokemon); // notify parent
      setForm({
        name: "",
        description: "",
        type: "NORMAL",
        hp: 50,
        imageUrl: "",
      });
      setError(""); // clear any previous errors
    } catch (err) {
      console.error("Failed to add Pokemon:", err);
      setError("Failed to add Pokémon. Please try again.");
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        display: "flex",
        gap: "12px",
        flexWrap: "wrap",
        margin: "20px",
      }}
    >
      <input
        type="text"
        name="name"
        value={form.name}
        onChange={handleChange}
        placeholder="Name"
        required
      />
      <input
        type="text"
        name="description"
        value={form.description}
        onChange={handleChange}
        placeholder="Description"
      />
      <select name="type" value={form.type} onChange={handleChange}>
        <option value="NORMAL">NORMAL</option>
        <option value="FIRE">FIRE</option>
        <option value="WATER">WATER</option>
        <option value="GRASS">GRASS</option>
        <option value="ELECTRIC">ELECTRIC</option>
      </select>
      <input
        type="number"
        name="hp"
        value={form.hp}
        onChange={handleChange}
        placeholder="HP"
      />
      <input
        type="text"
        name="imageUrl"
        value={form.imageUrl}
        onChange={handleChange}
        placeholder="Image URL"
      />
      <button type="submit">Add Pokémon</button>
      {error && <p style={{ color: "red" }}>{error}</p>}
    </form>
  );
}
